import React,{Fragment, useState} from 'react';

const Register = ()=>{
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    return(
<Fragment>
<form className='needs-validation container border p-5 mt-5'>
    <h1  style={{textAlign : 'center'}}>Register</h1>
    <hr className='mb-3'/>
  <div class="form-group">
    <label for="exampleInputEmail1">Name</label>
    <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" onChange={e=>setUsername(e.target.value)} required/>
  </div>
  <div class="form-group">
    <label for="exampleInputEmail1">Email address</label>
    <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" onChange={e=>setEmail(e.target.value)} required/>
    <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
  </div>
  <div class="form-group">
    <label for="exampleInputPassword1">Password</label>
    <input type="password" class="form-control" id="exampleInputPassword1" onChange={e=>setPassword(e.target.value)} required/>
  </div>
  <button type="submit" class="btn btn-lg btn-info mt-2">Submit</button>
</form>
</Fragment>
    );
};

export default Register;
