import React,{Fragment, useState} from 'react';

const Register = ()=>{
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const RegisterUser = async e =>{
        try {
            const body = {username, email, password};
            const query = await fetch('http://localhost:5000/auth/register',{
                method : 'POST',
                headers : {'Content-Type' : 'application/json'},
                body : JSON.stringify(body)
            });
            const response = await query.json();
            console.log(response);
        } catch (error) {
            console.log(error.message);
        }
    };
    return(
<Fragment>
<form className='needs-validation container border p-5 mt-5' onSubmit={RegisterUser}>
    <h1  style={{textAlign : 'center'}}>Register</h1>
    <hr className='mb-3'/>
  <div class="form-group">
    <label for="exampleInputEmail1">Name</label>
    <input type="text" class="form-control" aria-describedby="emailHelp" onChange={e=>setUsername(e.target.value)} required/>
  </div>
  <div class="form-group">
    <label for="exampleInputEmail1">Email address</label>
    <input type="email" class="form-control" aria-describedby="emailHelp" onChange={e=>setEmail(e.target.value)} required/>
    <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
  </div>
  <div class="form-group">
    <label for="exampleInputPassword1">Password</label>
    <input type="password" class="form-control"onChange={e=>setPassword (e.target.value)} required/>
  </div>
  <button type="submit" class="btn btn-lg btn-info mt-2">Submit</button>
</form>
</Fragment>
    );
};

export default Register;
